	private void WriteException(Exception ex) 
	{
		using (var sr = new StringReader(ex.ToString()))
		{
			string line;
			while ((line = sr.ReadLine()) != null)
			{
				this.Write(WebUtility.HtmlEncode(line));
				this.Write("<br />");
			}
		}
	}

